$(function(){$(".del").click(function(a){var t=$(a.target).data("id"),i=$(".item-id-"+t);$.ajax({type:"DELETE",url:"/admin/movie/list?id="+t}).done(function(a){1===a.success&&i.length>0&&i.remove()})}),$("#douban").blur(function(){var a=$(this).val();a&&$.ajax({url:"https://api.douban.com/v2/movie/subject/"+a,cache:!0,type:"get",dataType:"jsonp",crossDomain:!0,jsonp:"callback",success:function(a){$("#inputTitle").val(a.title),$("#inputDoctor").val(a.directors[0].name),$("#inputCountry").val(a.countries[0]),$("#inputPoster").val(a.images.large),$("#inputYear").val(a.year),$("#inputSummary").val(a.summary)}})})});